INSERT INTO catalogs VALUES('00004dd4e368','El Calafate','el-calafate','magellania-org','superadmin','organization',NULL,'{"version":"1.2.0","region":"El Calafate","templates":[{"id":"custom_1763574533460","name":"Трансфер Аэропорт In/Out FTE","category":"transfer","description":"","contractor":"","icon":"🚐","price":120,"region":"El Calafate"},{"id":"custom_1763574638431","name":"El espíritu De Los Glaciares (Круиз)","category":"_","description":"Crucero “Espíritu de los Glaciares” — роскошная экспедиция 2 дня / 1 ночь на корабле Santa Cruz (38 пассажиров, 19 кают). Старт и финиш: Puerto La Soledad (Paraje Punta Bandera). Включено: питание full board с напитками, каюта с санузлом и внешним видом, гид исп/англ, Wi-Fi. Не включено: вход в Parque Nacional. Доступны две программы в зависимости от даты: Circuito A (Puesto de las Vacas, ночёвка в Bahía Las Vacas, трек к mirador Glaciar Spegazzini, навигация у Spegazzini и Brazo Upsala, айсберги) и Circuito B (Seno Mayo, лесная тропа и каскад, ночёвка Bahía Toro, навигация к Glaciar Perito Moreno, пасареллы, северная стена). Продолжительность экспедиции — 22 часа: посадка 18:00, высадка 16:00 следующего дня. Актуально для гостей старше 5 лет, не подходит при ограниченной подвижности.","contractor":"https://crucerosmarpatag.com","icon":"🛳️","price":600,"region":"El Calafate"},{"id":"custom_1763575121305","name":"Национальный парк Перито Морено (весь день)","category":"transfer","description":"","contractor":"","icon":"🚐","price":750,"region":"El Calafate"},{"id":"custom_1763575582911","name":"Билеты Перито Морено 1 день","category":"activity","description":"","contractor":"","icon":"🎫","price":30,"region":"El Calafate"},{"id":"custom_1763575651583","name":"Билеты Перито Морено Flexipass 3 day","category":"transfer","description":"","contractor":"","icon":"🎫","price":60,"region":"El Calafate"},{"id":"custom_1763575947068","name":"Трансфер Эль калафате - Эль чальтен минивэн","category":"transfer","description":"","contractor":"","icon":"🚐","price":600,"region":"El Calafate"},{"id":"custom_1763575979346","name":"Трансфер Эль калафате - Эль чальтен спринтер","category":"transfer","description":"","contractor":"","icon":"🚐","price":700,"region":"El Calafate"},{"id":"custom_1763596224560","name":"Perito Moreno Kayak Experience (Excursion Only)","category":"_","description":"Каякинг перед ледником Перито Морено в Lago Argentino. Экскурсия проходит круглый год, кроме июня и июля. Длительность — около 3 часов на месте; время в каяке ~1,5 часа (зависит от погоды). Группа до 16 человек, минимум 2 для подтверждения. Участники — от 14 лет (до 18 в сопровождении взрослых). Сложность — средняя.  Начало в 10:00 в офисе на нижней парковке ледника Perito Moreno. Участники получают полный комплект экипировки (сухой костюм Gore-Tex с латексными манжетами, термокостюм, ботинки, спасжилет). Перед выходом проводится инструктаж и разминка. Каякинг выполняется на устойчивых двойных каяках. После возвращения — горячие напитки и snacks.  Включено: ~1,5 часа каяка, экипировка, инструктаж, горячие напитки и перекус. Не включено: вход в Parque Nacional Los Glaciares (оплата наличными).  Дополнительно: не требуется опыт; гиды могут отменить выход при небезопасных условиях; рекомендуется тёплая одежда под термокостюм.","contractor":"https://miloutdoor.com.ar","icon":"🛳️","price":215,"region":"El Calafate"},{"id":"custom_1763596280828","name":"Perito Moreno Kayak Experience — Full Day (с трансфером + vianda)","category":"_","description":"Экскурсия на двойных каяках перед ледником Перито Морено (Lago Argentino). Включает ~1,5 часа каякинга у фронта ледника и ~2 часа на пасарелах. Работает круглый год, за исключением июня и июля; выходы ежедневно, кроме четверга. Длительность: полный день (сбор 08:00 в Эль Калафате, возврат вечером). Группа до 16 человек, минимум для подтверждения — 2 человека. Участники: от 14 лет (до 18 в сопровождении взрослых).  Программа включает трансфер из Эль Калафате (74 км), инструктаж, выдачу полного комплекта экипировки (сухой костюм Gore-Tex, термокостюм, ботинки, спасжилет), каякинг в двойных устойчивых каяках, горячие напитки и vianda/snacks.  Не включено: вход в Parque Nacional Los Glaciares (оплата наличными).  Экскурсия не требует опыта. Погодные условия могут влиять на время и возможность выхода; решение принимает гид.","contractor":"https://miloutdoor.com.ar","icon":"🛳️","price":274,"region":"El Calafate"},{"id":"custom_1763597225924","name":"Trekking Laguna Torre","category":"cat_1763576164994","description":"Trekking Laguna Torre — дневной маршрут средней сложности, старт из El Chaltén. Поход идёт по Sendero Laguna Torre и включает подъём по боковой морене до её гребня, откуда открываются панорамные виды на Glaciar Grande и Cerro Torre. По пути трек пересекает леса lenga, ручьи и зону campamento De Agostini. Финал — у Laguna Torre с отражением пиков Egger, Standhardt и Cerro Torre в её водах, а при желании можно подняться дальше по гребню морены до Mirador Maestri. Протяжённость ~18 км туда-обратно, время — около 7–8 часов, набор высоты примерно 500 м. Уровень — moderado, подходит для людей с базовой физической подготовкой. Рекомендуется треккинговая обувь и защита от ветра и дождя.","contractor":"","icon":"🏔️","price":1,"region":"El Calafate"},{"id":"custom_1763597392142","name":"Трансфер","category":"transfer","description":"","contractor":"","icon":"🚐","price":70,"region":"El Calafate"},{"id":"custom_1763597973241","name":"Aventura Viedma","category":"_","description":"Aventura Viedma — навигация и короткий трек к одному из крупнейших ледников региона. Старт из Puerto Bahía Túnel, в 18 км от El Chaltén. Катамаран проходит по Lago Viedma с видами на Macizo Huemul и береговые скалы. Через 40 минут судно заходит в Bahía Cabo de Hornos, где участники делают лёгкий трек до обзорной точки на Península Cabo de Hornos с панорамой Bahía de los Témpanos и айсбергами на фоне Cordillera de los Andes. Затем следует приближение к frente del Glaciar Viedma: высокая фронтальная стена льда с ярко выраженными трещинами, глубокими оттенками синего и динамическим ледовым рельефом. Гид поясняет особенности образования ледника и его движения. После остановки и наблюдений маршрут возвращается по озеру к Puerto Bahía Túnel. Продолжительность — около трех с половиной часов, выезды круглый год.","contractor":"https://www.solopatagonia.com","icon":"🛳️","price":120,"region":"El Calafate"},{"id":"custom_1763598071687","name":"Aventura Viedma (VIP Capitains Club)","category":"_","description":"Aventura Viedma — навигация и лёгкий трек к леднику Viedma и причудливым панорамам Патагонии. Старт из Puerto Bahía Túnel, в 18 км от El Chaltén. Катамаран идёт по облицованным бирюзой водам Lago Viedma, предлагая виды на Macizo Huemul и скалистые берега. После примерно 40 минут навигации высадка в Bahía Cabo de Hornos и короткий трек до обзорной точки на Península Cabo de Hornos, откуда открывается вид на Bahía de los Témpanos с плавающими айсбергами и Andes на заднем плане. Затем судно подходит максимально близко к фронту Glaciar Viedma: массивная ледяная стена, с её глубокими синими оттенками и изменчивой структурой, гид рассказывает о её происхождении. После этого — обратный путь по озеру к Puerto Bahía Túnel. Времена: посадка в 10:30, отход катамарана в 11:00, возвращение примерно в 14:30. Выходы доступны круглый год, трансфер El Chaltén — Puerto Bahía Túnel возможен по запросу.","contractor":"https://www.solopatagonia.com","icon":"🛳️","price":220,"region":"El Calafate"},{"id":"custom_1763599924905","name":"Minitrekking 2, Glaciar Perito Moreno","category":"cat_1763599848757","description":"Удлинённая версия классического ледового трекинга на Glaciar Perito Moreno, организуемая Hielo y Aventura (создатели оригинального Minitrekking с 1989 года). Продолжительность около 10 часов. Старт — El Calafate (опциональный трансфер). Программа: посадка в порту Bajo de las Sombras, 10-минутная навигация по Lago Rico к Bahía Puma, получение снаряжения (кошки и каска), далее ~1 час подхода по берегу и морене к краю льда. На леднике — около 1 часа движения в группах до 20 человек (1 гид на 10 участников), маршрут средней/высокой сложности, общий километраж около 5,5 км (морена + лёд). Формы рельефа: трещины, moulins, серраки, голубые лагуны. После схода — возвращение по морене, переправа обратно и посещение пасарелл (если выбран трансфер). Ограничения: 18–55 лет, не подходит людям с низкой физической подготовкой. Не включает питание, вход в Parque Nacional. Требуются непромокаемые треккинговые ботинки, тёплая одежда, перчатки, солнцезащита, вода и еда.","contractor":"https://www.hieloyaventura.com","icon":"🏔️","price":260,"region":"El Calafate"},{"id":"custom_1763600142113","name":"Minitrekking, Glaciar Perito Moreno","category":"cat_1763599848757","description":"Пешеходный ледовый треккинг по леднику Перито Морено с опытными гидами, включая навигацию по Lago Rico и прогулку по берегу озера, морене и лесу. Во время 3-часового треккинга (~1 час по льду) участники наблюдают глубокие трещины, серраки, голубые лунки и ледяные лагуны, получают инструкции по безопасности, изучают гляциологию и делают остановки на смотровых точках. Группы до 20 человек, один гид на 10 туристов, умеренная физическая нагрузка. Продолжительность всего тура около 10 часов с трансфером, включая часовой визит на смотровые дорожки. Рекомендуемая экипировка: теплая одежда, непромокаемая куртка и обувь, солнцезащитные очки, перчатки, еда и вода. Необходимо иметь билет в Национальный парк","contractor":"https://www.hieloyaventura.com","icon":"🏔️","price":260,"region":"El Calafate"},{"id":"custom_1763600226712","name":"Safari Azul, Glaciar Perito Moreno","category":"cat_1763599848757","description":"Лёгкая экскурсия для всех, кто хочет увидеть ледник Перито Морено с воды и близко к льду, без выхода на сам ледник. Трансфер из Эль-Калафате, 20 минутная навигация по Lago Rico до берега, прогулка около 1,5 км по берегу озера и лесу с панорамными видами на ледник и возможностью наблюдать откалы льда. Включены профессиональные гиды, фотографирование, остановки на смотровых площадках. Продолжительность тура около 9 часов с трансфером и визитом к смотровым дорожкам. Необходим билет в Национальный парк. Рекомендуемая экипировка: тёплая одежда, дождевик, непромокаемая обувь, солнцезащитные очки, перчатки, еда и вода.","contractor":"https://www.hieloyaventura.com","icon":"🏔️","price":150,"region":"El Calafate"},{"id":"custom_1763600314417","name":"Big Ice, Glaciar Perito Moreno","category":"cat_1763599848757","description":"Экскурсия высокого уровня сложности с выходом на ледник Перито Морено. Трансфер из Эль-Калафате, 20 минутная навигация по Lago Rico до противоположного берега, пеший маршрут по морене около 2 часов с панорамными видами, затем прогулка по льду с крампонами и шлемами в группах до 10 человек под руководством гидов (примерно 3 часа по леднику с осмотром трещин, серяков, голубых озёр и ледяных пещер), обед на льду, возвращение по тому же пути и навигация вдоль южной стены ледника. Продолжительность тура около 12 часов, трансфер и визит к смотровым дорожкам включены. Рекомендуемая экипировка: тёплая одежда, дождевик, непромокаемая обувь, рюкзак 40 л, солнцезащитные очки, перчатки, еда и вода. Подходит для 18–50 лет, высокая физическая нагрузка.","contractor":"https://www.hieloyaventura.com","icon":"🏔️","price":503,"region":"El Calafate"}],"categories":[{"id":"transfer","name":"Трансфер","icon":"🚗"},{"id":"undefined","name":"Не определено","icon":"❓","system":true},{"id":"accommodation","name":"Отели","icon":"🏨"},{"id":"guide","name":"Гиды","icon":"👨‍🏫"},{"id":"activity","name":"Активности","icon":"🏛️"},{"id":"service","name":"Сервис","icon":"🔧"},{"id":"cat_1763576164994","name":"Треккинги","icon":"🏛️"},{"id":"_","name":"Водные активности","icon":"🏛️"},{"id":"cat_1763599848757","name":"Ледники","icon":"🏛️"}],"updated_at":"2025-11-27T01:20:36.233Z"}','1.2.0','2.3.0',6,NULL,'El Calafate',17,0,1764154992,